let texto =  $input.first().json.output || '';

// Remove aspas externas se houver
texto = texto.replace(/^"(.*)"$/, '$1');

// Substitui "R$" por "reais"
texto = texto.replace(/R\$/g, 'reais');

// Remove quebras de linha e múltiplos espaços
texto = texto.replace(/[\r\n]+/g, ' ');           // quebra de linha → espaço
texto = texto.replace(/\s\s+/g, ' ');             // múltiplos espaços → um espaço

// Remove espaços no início e fim
texto = texto.trim();

// Remove caracteres de controle invisíveis (exceto espaço normal)
texto = texto.replace(/[\u0000-\u001F\u007F]+/g, '');

// Escapa aspas internas se for necessário usar com JSON.stringify (opcional)
texto = texto.replace(/"/g, '\\"');

return [
  {
    json: {
      roteiro: texto
    }
  }
];

const raw = $input.first().json.message.content;

// Corrige a string com aspas escapadas e quebras de linha
const cleaned = raw.replace(/\\n/g, '').replace(/\\"/g, '"');

// Transforma a string JSON em objeto real
const parsed = JSON.parse(cleaned);

// Retorna cada prompt como um item separado
return parsed.image_prompts.map((item, index) => ({
  json: {
    ...item,
    index: index + 1
  }
}));

const narracaoIndex = $itemIndex;
const words = $json.words;

if (!Array.isArray(words)) {
  throw new Error("words inválidos");
}

function dividirPorPalavras(wordsArray) {
  const blocos = [];
  let atual = [];
  let blocoStart = wordsArray.length > 0 ? wordsArray[0].start : 0;

  wordsArray.forEach((wordObj, index) => {
    atual.push(wordObj.word);

    const ehUltima = index === wordsArray.length - 1;
    const passouLimite = atual.length >= 7 || ehUltima;

    if (passouLimite) {
      const blocoTexto = atual.join(' ');
      const blocoEnd = wordObj.end;

      blocos.push({
        text: blocoTexto,
        startTime: blocoStart,
        endTime: blocoEnd
      });

      blocoStart = blocoEnd;
      atual = [];
    }
  });

  return blocos;
}

const blocosFinal = dividirPorPalavras(words);

return {
  json: {
    narracaoIndex,
    blocos: blocosFinal,
    words
  }
};

/**
 * Formata segundos para o padrão de tempo do ASS (H:MM:SS.cs).
 */
function formatAssTime(seconds) {
  const ms = Math.floor((seconds % 1) * 100);
  const totalSeconds = Math.floor(seconds);
  const h = String(Math.floor(totalSeconds / 3600)).padStart(1, '0');
  const m = String(Math.floor((totalSeconds % 3600) / 60)).padStart(2, '0');
  const s = String(totalSeconds % 60).padStart(2, '0');
  const cs = String(ms).padStart(2, '0');
  return `${h}:${m}:${s}.${cs}`;
}

/**
 * Gera o texto de uma linha com tags de duração de karaoke {\k...}.
 * As cores são herdadas do Estilo definido em gerarAssCompleto.
 */
function gerarKaraokeLinha(wordsSegmentadas, palavrasBloco) {
  return wordsSegmentadas.map(palavra => {
    const match = palavrasBloco.find(w =>
      w.word.replace(/[,.!?;]/g, '').toLowerCase() ===
      palavra.replace(/[,.!?;]/g, '').toLowerCase()
    );
    if (match) {
      const duracao = Math.round((match.end - match.start) * 100);
      return `{\\k${duracao}}${match.word}`;
    } else {
      return palavra;
    }
  }).join(' ');
}

/**
 * Cria uma ou mais linhas de diálogo para um segmento de texto.
 */
function gerarLinhasPorSegmento(bloco, words) {
  // A função formatAssTime é chamada aqui.
  const inicio = formatAssTime(bloco.startTime);
  const fim = formatAssTime(bloco.endTime);

  const palavrasSegmento = bloco.text.trim().split(/\s+/);
  const palavrasBloco = words.filter(w => w.start >= bloco.startTime && w.end <= bloco.endTime);

  const linhaTexto = gerarKaraokeLinha(palavrasSegmento, palavrasBloco);

  return [`Dialogue: 0,${inicio},${fim},Default,,0,0,0,,${linhaTexto}`];
}

/**
 * Junta todas as partes e gera o conteúdo completo do arquivo .ass.
 * Define os estilos que controlam as cores do karaoke.
 */
function gerarAssCompleto(blocos, words, index) {
  const assHeader = `[Script Info]
Title: Letra ${String(index).padStart(2, '0')}
ScriptType: v4.00+
PlayResX: 1280
PlayResY: 720

[V4+ Styles]
Format: Name, Fontname, Fontsize, PrimaryColour, SecondaryColour, OutlineColour, BackColour, Bold, Italic, Underline, StrikeOut, ScaleX, ScaleY, Spacing, Angle, BorderStyle, Outline, Shadow, Alignment, MarginL, MarginR, MarginV, Encoding
Style: Default,Arial,60,&H0000FFFF,&H00FFFFFF,&H00000000,&H00000000,-1,0,0,0,100,100,0,0,1,2,1,2,30,30,80,1

[Events]
Format: Layer, Start, End, Style, Name, MarginL, MarginR, MarginV, Effect, Text`;

  const linhas = blocos.flatMap(bloco => gerarLinhasPorSegmento(bloco, words));
  return `${assHeader}\n${linhas.join('\n')}`;
}

// === Lógica principal de execução ===
const blocos = $json.blocos;
const words = $json.words;
const musicaIndex = $json.musicaIndex || 1;

const ass = gerarAssCompleto(blocos, words, musicaIndex);
const buffer = Buffer.from(ass, 'utf-8');

return {
  binary: {
    data: {
      data: buffer.toString('base64'),
      mimeType: 'text/x-ass',
      fileName: `legenda_${musicaIndex}.ass`
    }
  }
};

const duracaoNarracao = parseInt($('NARRACAO').first().json.tempo_s);
const duracaoMinimaPorMusica = 150;

// Calcula exatamente quantas músicas são necessárias para cobrir a narração
const quantidadeMusicas = Math.ceil(duracaoNarracao / duracaoMinimaPorMusica);

return [{
  json: {
    quantidade_musicas_para_baixar: quantidadeMusicas
  }
}];

function normalizeName(name) {
  return name.replace(/\s*\(\d+\)(?=\.\w+$)/, '');
}

const uniqueMap = new Map();

// Remove duplicados com base no nome normalizado
for (const item of items) {
  const filename = item.json.name || item.json.file || '';
  const baseName = normalizeName(filename);

  if (!uniqueMap.has(baseName)) {
    uniqueMap.set(baseName, item);
  }
}

// Pega os únicos e embaralha
const uniqueItems = Array.from(uniqueMap.values());
const shuffled = uniqueItems.sort(() => 0.5 - Math.random());

// Retorna apenas 2 músicas
return shuffled.slice(0, $('Quantas musicas preciso').first().json.quantidade_musicas_para_baixar);

const diretorio = "/tmp/youtubevideos/" + $('DIRETÓRIO').first().json.diretorio_slug;
const totalMusicas = $('Quantas musicas preciso').first().json.quantidade_musicas_para_baixar;

let comando;

if (totalMusicas === 1) {
  const path = `${diretorio}/musica0.mp3`;
  // Apenas copia o arquivo para manter a mesma saída esperada
  comando = `cp ${path} ${diretorio}/musica_unida.mp3`;
} else {
  let inputs = '';
  let concatList = '';

  for (let i = 0; i < totalMusicas; i++) {
    const path = `${diretorio}/musica${i}.mp3`;
    inputs += `-i ${path} `;
    concatList += `[${i}:a]`;
  }

  const filter = `${concatList}concat=n=${totalMusicas}:v=0:a=1[outa]`;
  comando = `ffmpeg ${inputs}-filter_complex "${filter}" -map "[outa]" -y ${diretorio}/musica_unida.mp3`;
}

return [{
  json: {
    ffmpeg_command: comando
  }
}];

const diretorio = "/tmp/youtubevideos/"+$('DIRETÓRIO').first().json.diretorio_slug;

const narracao = `${diretorio}/narracao.mp3`;
const musica = `${diretorio}/musica_unida_baixa.mp3`;
const output = `${diretorio}/audio_mixado_final.mp3`;

const comando = `ffmpeg \
-i "${narracao}" \
-i "${musica}" \
-filter_complex "[0:a][1:a]amix=inputs=2:duration=first[aout]" \
-map "[aout]" \
-c:a libmp3lame -q:a 2 -y "${output}"`;

return [{
  json: {
    ffmpeg_command: comando
  }
}];

const seconds = parseFloat($input.first().json.stdout);
const rounded = Math.round(seconds);

return [{ duracao_em_segundos: rounded }];

