    }
  }
]

const corrigido = items.map(item => {
  const corrigidoJSON = JSON.parse(item.json.output);
  return corrigidoJSON;
});

return corrigido;

// Função que quebra o texto em linhas sem cortar palavras
function quebrarTextoEmLinhas(texto, maxPorLinha) {
  const palavras = texto.split(' ');
  const linhas = [];
  let linhaAtual = '';

  for (const palavra of palavras) {
    if ((linhaAtual + ' ' + palavra).trim().length > maxPorLinha) {
      linhas.push(linhaAtual.trim());
      linhaAtual = palavra;
    } else {
      linhaAtual += ' ' + palavra;
    }
  }

  if (linhaAtual) {
    linhas.push(linhaAtual.trim());
  }

  return linhas;
}

const slug = $('DIRETÓRIO').first().json.diretorio_slug;
const textoTitulo = $('Faz o parser').first().json.texto;

const linhas = quebrarTextoEmLinhas(textoTitulo, 22);
const fonte = "/tmp/youtubevideos/montserrat-black.ttf";

const imagemEntrada = `/tmp/youtubevideos/${slug}/thumbnail.jpg`;
const imagemSaida = `/tmp/youtubevideos/${slug}/thumbnail_final.jpg`;

// Altura base
const fontSize = textoTitulo.length > 50 ? 70 : 90;
const espacamento = Math.round(fontSize * 1.4); // 1.4x o tamanho da fonte
const alturaInicial = 400 - ((linhas.length - 1) * espacamento) / 2;

// Monta todos os filtros drawtext para cada linha
const filtrosDrawtext = linhas.map((linha, i) => {
  const texto = linha.replace(/'/g, "\\'").replace(/:/g, '\\:');
  const y = alturaInicial + i * espacamento;

  // Linha 2 (índice 1) em vermelho, as demais em amarelo
  const cor = i === 1 ? '#FF0000' : '#FFFF00';

  return `drawtext=text='${texto}':fontfile='${fonte}':fontcolor=${cor}:fontsize=${fontSize}:shadowcolor=black:shadowx=2:shadowy=2:x=(w-text_w)/2:y=${y}`;
}).join(',');

// Comando final
const comando = `ffmpeg -i "${imagemEntrada}" -vf "${filtrosDrawtext}" -y "${imagemSaida}"`;

return [{
  json: {
    ffmpeg_command: comando
  }
}];
